$query_tables['ptsverticalmenu']['ptsverticalmenu'] = 'CREATE TABLE IF NOT EXISTS  `'._DB_PREFIX_.'ptsverticalmenu` (
  `id_ptsverticalmenu` int(11) NOT NULL AUTO_INCREMENT,
